import { BrowserRouter, Routes, Route} from "react-router-dom";
import { useState, useEffect } from "react";
import Home from "./pages/Home";
import Header from "./Components/Header";
import About from "./pages/About";
import Namecard from "./Components/Namecard";
import Searchbar from "./Components/Searchbar";
import Footer from "./Components/Footer";
import TagBar from "./Components/TagBar";

import { processDataAndUpdateCounts } from './dataProcessor'; // Import the data processing function

function App() {
  const [heading, setHeading] =useState("Home")

  const [tagsData, setTagsData] = useState([]);
  const [postsData, setPostsData] = useState([]);

  useEffect(() => {
    // Load tags.json
    fetch('path/to/tags.json')
      .then((response) => response.json())
      .then((data) => {
        setTagsData(data);
      });

    // Load posts.json
    fetch('path/to/posts.json')
      .then((response) => response.json())
      .then((data) => {
        setPostsData(data);
      });
  }, []);

  useEffect(() => {
    // Call the data processing function and update tagsData
    const updatedTagsData = processDataAndUpdateCounts(tagsData, postsData);
    setTagsData(updatedTagsData);
  }, [tagsData, postsData]);

  const getHeading=(e)=>{
    setHeading(e)

  }


  return (
    <div className="App">
      <BrowserRouter>
        <Header f={getHeading} />

        <div className="quote">
          <p className="p1">-------{heading}</p>
          <p className="p2">在这道路的前方，大概就是，美好的每一天。</p>
        </div>

        <div className="pages">
          <Routes>
            <Route path="/" element={<Home/> } />
            <Route path="/about" element={<About/>} />
          </Routes>
          <div className="sidebar">
            <Namecard />
            <div style={{padding: '40px'}}/>
            <Searchbar f={getHeading}/>
            <div style={{paddingBottom: '40px'}}/>
            <TagBar/>
          </div>
        </div>
        <Footer/>
      </BrowserRouter>
    </div>
  );
}

export default App;
