import { Link } from "react-router-dom";
import styles from "./styles.module.css";
import { useState } from "react";

const Navbar = (props) => {
  const [open, setOpen] = useState(false);

  
const dropdownMenuAnimation = {
  animationName: 'downOut',
  animationDuration: '300ms',
  animationTimingFunction: 'ease-in-out',
  animationFillMode: 'forwards',
};

const downOutKeyframes = {
  '0%': {
    transform: 'translateZ(200px) translateY(40px)',
  },
  '80%': {
    transform: 'translateZ(-10px) translateY(0px)',
  },
  '100%': {
    transform: 'translateZ(0px) translateY(0px)',
  },
};



  console.log(open);
  return (
    <header>
      <div className="container">
        <div className="container1">
          <Link to="/" style={{ color: props.color }}>
            <h1 className={styles.mainmenu}>Nana♡Blog</h1>
          </Link>
        </div>
        <div className="container2">
          <Link to="/" style={{ color: props.color }}>
            <h1 className={styles.navmenu}>Home</h1>
          </Link>

         {/* <Link
            style={{ color: props.color }}
            onMouseEnter={() => setOpen(true)}
            onMouseLeave={() => setOpen(false)}
            className="dropdown"
          >
            <h1 className={styles.navmenu}>
              Codes<span className="drop-down">▽</span>
            </h1>
            {/* {open ? (
              <ul className="menu">
              <li className="menu-item">
                <button>Menu 1</button>
              </li>
              <li className="menu-item">
                <button>Menu 2</button>
              </li>
            </ul>
            ) : null} 
            
            
            <div className={`menu ${open ? "open" : ""}`}>
              <button>Menu 1</button>
              <button>Menu 2</button>
            </div>
          </Link> */}

<div
      onMouseEnter={() => setOpen(true)}
      onMouseLeave={() => setOpen(false)}
      className={`dropdown ${open ? 'open' : ''}`}
    >
      <Link
        style={{ color: props.color }}
        className="navmenu"
      >
        Codes<span className="drop-down">▽</span>
      </Link>
      <div
        className={`menu ${open ? 'open' : ''}`}
        style={open ? dropdownMenuAnimation : {}}
      >
        <button>Menu 1</button>
        <button>Menu 2</button>
      </div>
      <style>
        {`
          @keyframes downOut {
            0% {
              transform: translateZ(200px) translateY(0px);
            }
            80% {
              transform: translateZ(-10px) translateY(20px);
            }
            100% {
              transform: translateZ(0px) translateY(0px);
            }
          }
        `}
      </style>
    </div>








          <Link style={{ color: props.color }}>
            <h1 className={styles.navmenu}>
              Shelf<span className="drop-down">▽</span>
            </h1>
          </Link>
          <Link to="/about" style={{ color: props.color }}>
            <h1 className={styles.navmenu}>About</h1>
          </Link>
          <Link style={{ color: props.color }}>
            <h1 id="jp" className={styles.navmenu}>
              素晴らしき日々
            </h1>
          </Link>
        </div>
      </div>
    </header>
  );
};

export default Navbar;
