    fetch("http://ip-api.com/json/?fields=3195137")
      .then((res) => res.json())
      .then((res) => {
        setUserTimezone(res.timezone); // Update user's timezone state
        const now = new Date();
        const utime = new Intl.DateTimeFormat("default", {
          timeZone: res.timezone,
          weekday: "long",
          year: "numeric",
          month: "long",
          day: "numeric",
          hour: "numeric",
          minute: "numeric",
