import { BrowserRouter, Routes, Route} from "react-router-dom";
import { useState } from "react";
import Home from "./pages/Home";
import Header from "./Components/Header";
import About from "./pages/About";
import Namecard from "./Components/Namecard";
import Searchbar from "./Components/Searchbar";

function App() {
  const [heading, setHeading] =useState("Home")
  const [searchbarHeading, setSearchbarHeading] = useState("Home");
  const [boo, setboo] = useState(true)

  const getHeading=(e)=>{
    if(boo) setHeading(e)

  }
  const getHeading2=(e)=>{
    setboo(false)
    setHeading(e)
  }
  const bo=(e)=>{
    setboo(e)
  }

  return (
    <div className="App">
      <BrowserRouter>
        <Header />

        <div className="quote">
          <p className="p1">-------{heading}</p>
          <p className="p2">在这道路的前方，大概就是，美好的每一天。</p>
        </div>

        <div className="pages">
          <Routes>
            <Route path="/" element={<Home function={getHeading} tf={bo}/> } />
            <Route path="/about" element={<About function={getHeading} tf={bo}/>} />
          </Routes>
          <div className="sidebar">
            <Namecard />
            <div style={{padding: '40px'}}/>
            <Searchbar function={getHeading2}/>
          </div>
        </div>
      </BrowserRouter>
    </div>
  );
}

export default App;
