import React, { useState, useEffect } from "react";

export default function FetchIP() {
  const [userTime, setUserTime] = useState("");
  const [userTimezone, setUserTimezone] = useState("");

  useEffect(() => {
    // Replace 'userTimezone' with the actual user's timezone obtained from the API
    var userTimezone;

    const options = {
      timeZone: userTimezone,
      weekday: "long",
      year: "numeric",
      month: "long",
      day: "numeric",
      hour: "numeric",
      minute: "numeric",
      second: "numeric",
    };

    fetch("http://ip-api.com/json/?fields=3195137")
      .then((res) => res.json())
      .then((res) => {
        userTimezone = res.timeZone;
      });

    const localTime = new Date().toLocaleString(undefined, options);
    setUserTime(localTime);
  }, []);

  return (
    <div>
      <p>Visitor's Local Time: {userTime.getHours()}</p>
    </div>
  );
}
