// dataProcessor.js

export function processDataAndUpdateCounts(tagsData, postsData) {
  const tagCounts = {};

  // Loop through the postsData and count posts for each majTag
  for (const post of postsData) {
    const majTag = post.majTag;

    if (tagCounts[majTag] === undefined) {
      tagCounts[majTag] = 1;
    } else {
      tagCounts[majTag]++;
    }
  }

  // Update the counts in tagsData
  const updatedTagsData = tagsData.map((tag) => ({
    ...tag,
    count: tagCounts[tag.name] || 0, // Use 0 if no posts found for the tag
  }));

  return updatedTagsData;
}
