import React, { useEffect, useState } from "react";
import Navbar from "./Navbar";
import a from "../images/banner1.png";
import b from "../images/banner2.png";
import c from "../images/bg.png"

export default function Header() {
  const [background, setBackground] = useState(b);
  const [fontColor, setFontColor] = useState();

  useEffect(() => {
    var today = new Date();
    var hour = today.getHours();
    console.log(hour)
    if (hour < 0 || hour >= 20) {
      // setBackground(b);
      setBackground(c);
      setFontColor(true)
    } else {
      setBackground(a);
      setBackground(c);
      setFontColor(false)
      console.log(hour);
    }
  },[background]);

  const headerStyle = {
    backgroundImage: `url(${background})`,
    // height: "310px",
    flexShrink: 0,
    backgroundSize: "cover",
    backgroundPosition: "center",
  };

  return (
    <div>
      <div style={headerStyle}>
        <Navbar color={fontColor ? "white" : "black"} />
      </div>
    </div>
  );
}
